    public static int sentenceCounter(String text){
        int counter = 0;
        String regex = "[a-zA-Z ,:';-]+.";
        Pattern p = Pattern.compile(regex);
        Matcher m =p.matcher(text);
        while (m.find()){
            counter++;
        }
        return counter;
    }

    public static int wordCounter3(String text){
        int counter = 0;
        String regex = "\\b[a-zA-Z]{3}\\b";
        Pattern p = Pattern.compile(regex);
        Matcher m =p.matcher(text);
        while (m.find()){
            counter++;
        }
        return counter;
    }
